/**
 * Get an icon SVG with a custom fill color
 * @param iconName - The name of the icon from iconMap
 * @param color - The color to use (can be a hex color, CSS variable, or 'currentColor')
 * @returns The SVG string with the specified fill color, or undefined if icon not found
 */
export function getIconWithColor(iconName: string, color: string = 'currentColor'): string | undefined {
    const iconSvg = iconMap[iconName];
    if (!iconSvg) return undefined;
    
    // Replace fill attributes in both the SVG element and path elements
    return iconSvg
        .replace(/fill="[^"]*"/g, `fill="${color}"`)
        .replace(/fill='[^']*'/g, `fill='${color}'`);
}